import React, { useState } from "react";
import styles from "./Quote.module.css";

const quotes: string[] = [
  "The best way to get started is to quit talking and begin doing.",
  "Don't let yesterday take up too much of today.",
  "It's not whether you get knocked down, it's whether you get up.",
  "Simplicity is the soul of efficiency.",
  "Experience is the name everyone gives to their mistakes.",
];

const QuoteGenerator: React.FC = () => {
  const [index, setIndex] = useState(0);
  const [fade, setFade] = useState(true);

  const handleNextQuote = () => {
    setFade(false); // fade out current quote
    setTimeout(() => {
      let newIndex = Math.floor(Math.random() * quotes.length);
      while (newIndex === index) newIndex = Math.floor(Math.random() * quotes.length);
      setIndex(newIndex);
      setFade(true); // fade in new quote
    }, 300);
  };

  return (
<div className="flex flex-col items-center min-h-screen bg-black p-6">
  {/* Spacer pushes quote to top */}
  <div className="flex-1 flex items-center justify-center">
    <p
      key={index}
      className={styles.quoteText}
      style={{
        color: "black",
        fontSize: "1.5rem",
        fontWeight: 600,
        textAlign: "center",
        opacity: fade ? 1 : 0,
        transition: "opacity 0.3s ease-in-out",
      }}
    >
      {quotes[index]}
    </p>
  </div>

  {/* Button at middle */}
  <div className="flex-1 flex items-center justify-center">
    <button
      onClick={handleNextQuote}
      className="px-6 py-3 bg-indigo-500 hover:bg-indigo-600 text-white font-semibold rounded-2xl transition"
    >
      Inspire Me âœ¨
    </button>
  </div>
</div>

  );
};

export default QuoteGenerator;
