import React, { useState, useEffect } from "react";
import AddQuoteForm from "./AddQuoteForm";

interface Quote {
  id: number;
  text: string;
  author: string | null;
  created_at: string;
}

const QuoteGenerator: React.FC = () => {
  const [quotes, setQuotes] = useState<Quote[]>([]);
  const [currentIndex, setCurrentIndex] = useState(0);

  // Fetch all quotes from backend
  const fetchQuotes = async () => {
    try {
      const response = await fetch("http://127.0.0.1:8000/quotes");
      const data = await response.json();
      setQuotes(data);
    } catch (error) {
      console.error("Error fetching quotes:", error);
    }
  };

  useEffect(() => {
    fetchQuotes();
  }, []);

  const handleNextQuote = () => {
    if (quotes.length === 0) return;
    setCurrentIndex((prevIndex) => (prevIndex + 1) % quotes.length);
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen">
      {/* Display current quote */}
      {quotes.length > 0 && (
        <div className="bg-white p-6 rounded shadow mb-4 text-center max-w-md">
          <p className="text-xl font-semibold">"{quotes[currentIndex].text}"</p>
          <p className="text-gray-600 mt-2">
            â€” {quotes[currentIndex].author || "Unknown"}
          </p>
        </div>
      )}

      {/* Next quote button */}
      <button
        onClick={handleNextQuote}
        className="bg-gradient-to-r from-purple-500 to-pink-500 text-white px-6 py-2 rounded shadow hover:opacity-90 mb-6"
      >
        Next Quote
      </button>

      {/* Add new quote form */}
      <div className="w-full max-w-md">
        <AddQuoteForm onQuoteAdded={fetchQuotes} />
      </div>
    </div>
  );
};

export default QuoteGenerator;
