import React, { useState } from "react";
import styles from "./Quote.module.css";

const quotes: string[] = [
  "The best way to get started is to quit talking and begin doing.",
  "Don't let yesterday take up too much of today.",
  "It's not whether you get knocked down, it's whether you get up.",
  "Simplicity is the soul of efficiency.",
  "Experience is the name everyone gives to their mistakes.",
];

const QuoteGenerator: React.FC = () => {
  const [index, setIndex] = useState(0);
  const [fade, setFade] = useState(true);

  const handleNextQuote = () => {
    setFade(false); // start fade out
    setTimeout(() => {
      let newIndex = Math.floor(Math.random() * quotes.length);
      while (newIndex === index) newIndex = Math.floor(Math.random() * quotes.length);
      setIndex(newIndex);
      setFade(true); // fade in new quote
    }, 300); // duration matches CSS
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-black p-6">
      <p
        className={`${styles.quoteText} text-white transition-opacity duration-300 ${
          fade ? "opacity-100" : "opacity-0"
        }`}
      >
        {quotes[index]}
      </p>

      <button
        onClick={handleNextQuote}
        className="mt-6 px-6 py-3 bg-indigo-500 hover:bg-indigo-600 text-white font-semibold rounded-2xl transition"
      >
        Inspire Me âœ¨
      </button>
    </div>
  );
};

export default QuoteGenerator;
