import React, { useState } from "react";

const QuoteGenerator: React.FC = () => {
  const [quote, setQuote] = useState<{ text: string; author?: string } | null>(null);
  const [loading, setLoading] = useState(false);

  const handleNextQuote = async () => {
    setLoading(true);
    try {
      const response = await fetch("http://127.0.0.1:8000/quotes/random");
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const randomQuote = await response.json();
      setQuote(randomQuote);
    } catch (error) {
      console.error("Error fetching random quote:", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      {quote ? (
        <p>
          “{quote.text}” — {quote.author || "Unknown"}
        </p>
      ) : (
        <p>Click the button to get inspired ✨</p>
      )}

      <button onClick={handleNextQuote} disabled={loading}>
        {loading ? "Loading..." : "Inspire Me ✨"}
      </button>
    </div>
  );
};

export default QuoteGenerator;
