import React, { useState } from "react";
import { AnimatePresence, motion, HTMLMotionProps } from "framer-motion";

const quotes: string[] = [
  "The best way to get started is to quit talking and begin doing.",
  "Don't let yesterday take up too much of today.",
  "It's not whether you get knocked down, it's whether you get up.",
  "Simplicity is the soul of efficiency.",
  "Experience is the name everyone gives to their mistakes.",
];

// Correctly typed motion <p>
const MotionP: React.FC<HTMLMotionProps<"p">> = motion.p;

const QuoteGenerator: React.FC = () => {
  const [index, setIndex] = useState(0);

  const handleNextQuote = () => {
    let newIndex = Math.floor(Math.random() * quotes.length);
    while (newIndex === index) newIndex = Math.floor(Math.random() * quotes.length);
    setIndex(newIndex);
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-900 p-6">
      <AnimatePresence mode="wait">
        <MotionP
          key={index}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: -20 }}
          transition={{ duration: 0.5 }}
          className="text-xl font-semibold text-center mb-6 text-white"
        >
          {quotes[index]}
        </MotionP>
      </AnimatePresence>
      <button
        onClick={handleNextQuote}
        className="px-6 py-2 bg-indigo-500 hover:bg-indigo-600 text-white font-semibold rounded-2xl transition"
      >
        Inspire Me âœ¨
      </button>
    </div>
  );
};

export default QuoteGenerator;
