import React, { useState } from "react";

const AddQuoteForm: React.FC<{ onQuoteAdded: () => void }> = ({ onQuoteAdded }) => {
  const [text, setText] = useState("");
  const [author, setAuthor] = useState("");

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    await fetch("http://localhost:5000/quotes", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ text, author }),
    });

    setText("");
    setAuthor("");
    onQuoteAdded();
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="flex flex-col items-center gap-3 mt-6 w-full max-w-md"
    >
      <input
        type="text"
        placeholder="Enter quote"
        value={text}
        onChange={(e) => setText(e.target.value)}
        className="w-full border p-2 rounded shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-400"
      />
      <input
        type="text"
        placeholder="Enter author"
        value={author}
        onChange={(e) => setAuthor(e.target.value)}
        className="w-full border p-2 rounded shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-400"
      />
      <button
        type="submit"
        className="px-6 py-2 rounded bg-green-500 text-white font-semibold hover:bg-green-600 transition"
      >
        Add Quote
      </button>
    </form>
  );
};

export default AddQuoteForm;
